package com.tools.common.utils.base64;

import cn.hutool.core.util.IdUtil;
import com.itextpdf.text.Document;
import com.itextpdf.text.DocumentException;
import com.itextpdf.text.Image;
import com.itextpdf.text.pdf.PdfWriter;
import org.springframework.web.multipart.MultipartFile;
import sun.misc.BASE64Decoder;

import java.io.File;
import java.io.FileInputStream;
import java.io.FileOutputStream;
import java.io.IOException;
import java.util.ArrayList;
import java.util.Base64;
import java.util.List;

/**
 * @Title: Base64ImageToPDF
 * @Author LinLuoChen
 * @ProjectName ToolCabinet
 * @Description: Base64 转换文件（图片，PDF）互转
 * @Date 2023/5/8 11:06
 */
public class Base64ImageToPDF {

    private static final Base64.Encoder ENCODER_64 = Base64.getEncoder();

    /**
     * 图片转pdf
     *
     * @param imgStrList 图片base64
     * @param target
     */
    public static String ImgChangePDF(List<String> imgStrList, String target) {
        String result = "";
        //创建一个文档对象
        Document doc = new Document();
        try {
            //定义输出文件的位置
            PdfWriter.getInstance(doc, new FileOutputStream(target));
            //开启文档
            doc.open();
            // 循环获取图片文件夹内的图片
            for (String imgStr : imgStrList) {
                if (imgStr == null) {
                    continue;
                }
                //路径
                MultipartFile multipartFile = base64MultipartFile(imgStr);
                Image img = Image.getInstance(multipartFile.getBytes());
                //获得宽高
                Float h = img.getHeight();
                Float w = img.getWidth();
                //统一压缩
                Integer percent = getPercent(h, w);
                //图片居中
                img.setAlignment(Image.MIDDLE);
                //百分比显示图
                img.scalePercent(percent);
                //设置高和宽的比例
                doc.add(img);
            }
            // 关闭文档
            if (doc != null) {
                doc.close();
            }
            //转Base64
            File pdfFile = new File(target);
            FileInputStream fileInputStream = new FileInputStream(pdfFile);
            byte[] byteBuff = new byte[(int) pdfFile.length()];
            fileInputStream.read(byteBuff);
            fileInputStream.close();
            result = ENCODER_64.encodeToString(byteBuff).trim();
            if (pdfFile != null) {
                pdfFile.delete();
            }
        } catch (IOException | DocumentException e) {
            e.printStackTrace();
        }
        return result;
    }

    /**
     * 压缩
     *
     * @param h
     * @param w
     * @return
     */
    public static Integer getPercent(Float h, Float w) {
        Integer g = 0;
        Float g2 = 0.0f;
        g2 = 480 / w * 100;
        g = Math.round(g2);
        return g;
    }

    public static MultipartFile base64MultipartFile(String base64) {
        try {
            String[] baseStr = base64.split(",");
            BASE64Decoder base64Decoder = new BASE64Decoder();
            byte[] b;
            b = base64Decoder.decodeBuffer(baseStr[0]);
            for (int i = 0; i < b.length; ++i) {
                if (b[i] < 0) {
                    b[i] += 256;
                }
            }
            return new BASE64DecodedMultipartFile(b, baseStr[0]);
        } catch (Exception e) {
            e.printStackTrace();
            return null;
        }
    }


    public static void main(String[] args) throws Exception {
        List<String> source = new ArrayList<String>();
        //这里我把图片base64存txt里面了，具体你们自己改
        source.add("");
        source.add("");
        String str = ImgChangePDF(source, IdUtil.simpleUUID());
        Base64Utils.base64ContentToFile(str, "D:\\downloads\\" + IdUtil.simpleUUID() + ".pdf");
    }

}
